var answers = JSON.parse(workflow.variables.u_answers);

var ipAddress = answers[0].answer.toString();
var type = workflow.variables.u_type.toString().toUpperCase();

var reverseDomainName = '';
var reverseZoneName = '';

if (type == 'A'){

	reverseDomainName = ipAddress.split('.').reverse().join('.')+".in-addr.arpa";
	reverseZoneName = reverseDomainName.substring(reverseDomainName.indexOf(".") + 1);

}else if (type == 'AAAA'){

	var arr = ipAddress.split(':');
	var reverseArr = arr.reverse();

	for(var i in reverseArr){
		//workflow.info("Arr "+ reverseArr[i].toString().split('').reverse().join('.'));
		if(reverseArr[i].length != 0){
			reverseDomainName += reverseArr[i].toString().split('').reverse().join('.') +'.';

			if(i != 0)
				reverseZoneName += reverseArr[i].toString().split('').reverse().join('.')+'.';	

		}
	}

	reverseDomainName += "ip6.arpa";
	reverseZoneName += "ip6.arpa";
}

workflow.scratchpad.revereseDomainName = reverseDomainName;
workflow.scratchpad.reverseZoneName = reverseZoneName;

workflow.scratchpad.reverseAnswers = '[{"answer":["'+ workflow.variables.u_domain + '"]}]';

